/*eslint-disable*/

import React, {useState, useEffect} from 'react';
import { Jumbotron, Button, Col, Row, Card, Container, Navbar, Nav } from 'react-bootstrap';
import './App.scss';
import { BrowserRouter, Link, Route, Switch, useHistory } from 'react-router-dom';

import datas from './data.js';
import Detail from './detail.js';
import {} from './practice.js';


function App() {

	return (
		<div className="App">

			<Counter />

			<TestRouter />

		</div>
	);
}

function Counter() {
  const [count, setCount] = useState(0);

  useEffect(() => {
    const id = setInterval(() => {
      setCount(prev => prev + 1); // Ïù¥ effectÎäî 'count' stateÏóê Îî∞Îùº Îã§Î¶ÖÎãàÎã§
    }, 1000);
    return () => clearInterval(id);
  }, []); // üî¥ Î≤ÑÍ∑∏: `count`Í∞Ä Ï¢ÖÏÜçÏÑ±ÏúºÎ°ú ÏßÄÏ†ïÎêòÏßÄ ÏïäÏïòÏäµÎãàÎã§

  return <h1>{count}</h1>;
}

function TestRouter(){
	let [post, setPost] = useState(datas);


	// option Í∞íÏóê Îî∞Îùº contentÎ•º Ï†ïÎ†¨. 0ÏùºÏãú title, 1ÏùºÏãú id
	function sortContent(params){
		let {option} = params;
		let newPost = [...post];

		if (option == 0){
			newPost.sort((data1, data2)=>{
				let d1 = data1.title.toUpperCase();
				let d2 = data2.title.toUpperCase();

				if (d1 < d2)
					return -1;
				else
					return 1;
			})
		}
		else if (option == 1){
			newPost.sort((data1, data2)=>{
				return data1.id - data2.id;
			})
		}

		setPost(newPost);
	}

	return(
	<div>
		<Navbar bg="light" expand="lg">
			<Navbar.Brand href="#home">Nav-Bar</Navbar.Brand>
			<Navbar.Toggle aria-controls="basic-navbar-nav" />
			<Navbar.Collapse id="basic-navbar-nav">
				<Nav className="mr-auto">
					<Nav.Link as={Link} to="/"> home </Nav.Link>
				</Nav>
			</Navbar.Collapse>
		</Navbar>

		<Switch>
			<Route path="/" exact>
				<Jumbotron className="siteJumbotron">
					<h1>Hello, world!</h1>
					<p>
					This is a simple hero unit, a simple jumbotron-style component for calling
					extra attention to featured content or information.
					</p>
					<p>
						<Button variant="primary">Learn more</Button>
					</p>
				</Jumbotron>
				<div>
					<Button variant="primary" className="m-3" onClick={() => {sortContent({option: 0})}}> Sort by Title</Button>
					<Button variant="primary" className="m-3" onClick={() => {sortContent({option: 1})}}> Sort by id</Button>
				</div>

				<CardContent post={post} />
			</Route>
			<Route path="/detail/:id">
				<Detail post={post}/>
			</Route>
		</Switch>

	</div>
	);
}

function CardContent(props){
	let {post} = props;
	let history = useHistory();

	return(
		<div>
			<Container>
				<Row>
					{
						post.map((data, i) => {
							return(
								<Col md={4} key={i}> 
									<Card><Card.Body>
										<Card.Title> {data.title} </Card.Title>
										<Card.Subtitle className="mb-2 text-muted">{data.price}</Card.Subtitle>
										<Card.Text>
											{data.content}
										</Card.Text>
										<Button onClick={()=>{history.push("/detail/" + data.id)}}> Detail </Button>
									</Card.Body></Card>
								</Col>
							)
						})
					}
				</Row>
			</Container>
		</div>
	);

}



export default App;
